import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';
import Slide from '@material-ui/core/Slide';
import Dialog from '@material-ui/core/Dialog';
import AppBar from '@material-ui/core/AppBar';
import IconButton from '@material-ui/core/IconButton';
import Toolbar from '@material-ui/core/Toolbar';
import CloseIcon from '@material-ui/icons/Close';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import TextField from '@material-ui/core/TextField';
import { fade } from '@material-ui/core/styles/colorManipulator';
import InputBase from '@material-ui/core/InputBase';
import SearchIcon from '@material-ui/icons/Search';

const styles = theme => ({
    root: {
        width: '100%',
        maxWidth: '100%',
        backgroundColor: theme.palette.background.paper,
    },
    inline: {
        display: 'inline',
    },
    fab: {
        position: 'absolute',
        bottom: theme.spacing.unit * 2,
        right: theme.spacing.unit * 2,
    },
    appBar: {
        position: 'relative',
    },
    flex: {
        flex: 1,
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing.unit,
            width: 'auto',
        },
    },
    searchIcon: {
        width: theme.spacing.unit * 9,
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    }
});
function Transition(props) {
    return <Slide direction="up" {...props} />;
}


class MailListReceive extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            open: false
        }
    }
    handleClickOpen = () => {
        this.setState({ open: true });
    };
    handleClose = () => {
        this.setState({ open: false });
    };

    render() {
        const { classes } = this.props;
        var messages = this.props.messages;
        const GroupsOfMessages = messages.map(message => {
            if (message['toUser'] === "tienle123") {
                   return <ListItem alignItems="flex-start" key={message["mailId"]}>
                        <ListItemAvatar>
                            <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
                        </ListItemAvatar>
                        <ListItemText
                            primary="Brunch this weekend?"
                            secondary={
                                <React.Fragment>
                                    <Typography component="span" className={classes.inline} color="textPrimary">
                                        {message["fromUser"]}
                                    </Typography>
                                    {" — " + message["content"]}
                                </React.Fragment>
                            }
                        />
                    </ListItem>
            }
        });

        return (
            <div>
                <List className={classes.root}>
            {GroupsOfMessages}
                </List>
                <Fab color="primary" aria-label="Add" className={classes.fab} >
                    <AddIcon onClick={this.handleClickOpen} />
                </Fab>
                <Dialog
                    fullScreen
                    open={this.state.open}
                    onClose={this.handleClose}
                    TransitionComponent={Transition}
                >
                <AppBar className={classes.appBar}>
                    <Toolbar>
                        <IconButton color="inherit" onClick={this.handleClose} aria-label="Close">
                            <CloseIcon />
                        </IconButton>
                        <Typography variant="h6" color="inherit" className={classes.flex}>
                            Compose Email
                        </Typography>
                        <Button color="inherit" onClick={this.handleClose}>
                            Send
                        </Button>
                    </Toolbar>
                </AppBar>
                <List>
                    <ListItem button>
                        <Toolbar>
                            <IconButton className={classes.menuButton} color="inherit" aria-label="Open drawer">
                            </IconButton>
                            <Typography className={classes.title} variant="h6" color="inherit" noWrap>
                                Material-UI
                            </Typography>
                            <div className={classes.grow} />
                            <div className={classes.search}>
                                <div className={classes.searchIcon}>
                                    <SearchIcon />
                                </div>
                                <InputBase
                                    placeholder="Search…"
                                    classes={{
                                        root: classes.inputRoot,
                                        input: classes.inputInput,
                                    }}
                                />
                            </div>
                        </Toolbar>
                    </ListItem>
                    <ListItem button>
                        {/*<ListItemText primary="Phone ringtone" secondary="Titania" />*/}
                        <TextField
                            id="outlined-full-width"
                            label="Title"
                            style={{ margin: 8 }}
                            placeholder="Title of the email"
                            required={true}
                            fullWidth
                            margin="normal"
                            variant="outlined"
                            InputLabelProps={{
                                shrink: true,
                            }}
                        />
                    </ListItem>
                    <Divider />
                    <ListItem button>
                        <TextField
                            id="outlined-textarea"
                            label="Content"
                            placeholder="Type your content"
                            autoFocus={true}
                            multiline={true}
                            rows={10}
                            required={true}
                            fullWidth
                            className={classes.textField}
                            margin="normal"
                            variant="outlined"
                        />
                    </ListItem>
                </List>
            </Dialog>
            </div>
        );
    }
}
MailListReceive.propTypes = {
    classes: PropTypes.object.isRequired,
};
export default withStyles(styles)(MailListReceive);
