import React from "react";
import SockJsClient from "react-stomp";
import { TalkBox } from "react-talk";
import MailList from "./MailList";
const DEVELOPMENT_URL = "http://localhost:8080";

export default class Email extends React.Component {
    constructor(props) {
        super(props);
        this.submit = this.submit.bind(this);
        this.onChangeMessage = this.onChangeMessage.bind(this);
        this.state = {
            clientConnected: false,
            messages: [],
            text: ''
        };
    }
    onChangeMessage(e){
        this.setState({text:e.target.value});
    }
    submit(e){
        e.preventDefault();
        try {
            this.clientRef.sendMessage("/app/all", JSON.stringify({"content": this.state.text, "fromUser": "thongtran715", "toUser": "tienle123"}));
            console.log("Sent message");
            return true;
        } catch(e) {
            return false;
        }
    }
    onMessageReceive = (msg, topic) => {
        console.log("Message from Service");
        let message = msg['content'];
        this.setState(prevState => ({
            messages: [...prevState.messages, message]
        }));
    };
    async componentWillMount() {
        var url = DEVELOPMENT_URL + "/mail/thongtran715";
        const response = await fetch(url);
        const body = await response.json();
        body.map((mail) => {
            this.setState({
                messages : [...this.state.messages, mail]
            })
        });
        return;
    };
    render() {
        return (
            <div>
                <form onSubmit={this.submit}>
                    <input type="text" onChange={this.onChangeMessage}/>
                    <button type="submit">Submit</button>
                </form>
                <MailList messages={this.state.messages}/>
                <SockJsClient url= {DEVELOPMENT_URL + "/ws"}   topics={["/topic/all"]}
                              onMessage={ this.onMessageReceive } ref={ (client) => { this.clientRef = client }}
                              debug={ false }/>
            </div>
        );
    }
}
