import React from "react";
import SockJsClient from "react-stomp";
import MailList from "./MailListReceive";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
const DEVELOPMENT_URL = "http://localhost:8080";

export default class Email extends React.Component {
    constructor(props) {
        super(props);
        this.submit = this.submit.bind(this);
        this.onChangeMessage = this.onChangeMessage.bind(this);
        this.fetchMessages = this.fetchMessages.bind(this);
        this.state = {
            clientConnected: false,
            messages: [],
            text: ''
        };
    }
    onChangeMessage(e){
        this.setState({text:e.target.value});
    }
    submit(e){
        e.preventDefault();
        try {
            this.clientRef.sendMessage("/app/all", JSON.stringify({"content": this.state.text, "fromUser": "thongtran715", "toUser": "tienle123"}));
            console.log("Sent message");
            return true;
        } catch(e) {
            return false;
        }
    }
    onMessageReceive = (msg, topic) => {
        console.log("Message from Service");
        let message = msg['content'];
        this.setState(prevState => ({
            messages: [...prevState.messages, message]
        }));
    };
    async fetchMessages(){
        const url = DEVELOPMENT_URL + "/mail/receive/tienle123";
        const response = await fetch(url);
        const body = await response.json();
        console.log(body);
        var meggs = [];
        for (let i = 0 ; i < body.length; ++i) {
            meggs.push(body[i]);
        }
        this.setState({
            messages: [...this.state.messages, ...meggs]
        });
    };
    async componentDidMount() {
        this.fetchMessages();
    }
    render() {
        const GroupsOfMessages = this.state.messages.map(message => {
            if (message['toUser'] === "tienle123") {
                return <ListItem alignItems="flex-start">
                    <ListItemAvatar>
                        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
                    </ListItemAvatar>
                    <ListItemText
                        primary="Brunch this weekend?"
                        secondary={
                            <React.Fragment>
                                <Typography component="span"  color="textPrimary">
                                    {message["fromUser"]}
                                </Typography>
                                {" — " + message["content"]}
                            </React.Fragment>
                        }
                    />
                </ListItem>
            }
        });
        return (
            <div>
                <form onSubmit={this.submit}>
                    <input type="text" onChange={this.onChangeMessage}/>
                    <button type="submit">Submit</button>
                </form>
                {/*<MailList messages={this.state.messages}/>*/}
                <List>
                    {GroupsOfMessages}
                </List>

                <SockJsClient url= {DEVELOPMENT_URL + "/ws"}   topics={["/topic/all"]}
                              onMessage={ this.onMessageReceive } ref={ (client) => { this.clientRef = client }}
                              debug={ false }/>

            </div>
        );
    }
}
