import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
const styles = theme => ({
    root: {
        width: '100%',
        maxWidth: '100%',
        backgroundColor: theme.palette.background.paper,
    },
    inline: {
        display: 'inline',
    },
});
class MailListReceive extends React.Component{
    render() {
        const { classes } = this.props;
        var messages = this.props.messages;
        const GroupsOfMessages = messages.map(message => {
            if (message['toUser'] === "tienle123") {
                   return <ListItem alignItems="flex-start">
                        <ListItemAvatar>
                            <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
                        </ListItemAvatar>
                        <ListItemText
                            primary="Brunch this weekend?"
                            secondary={
                                <React.Fragment>
                                    <Typography component="span" className={classes.inline} color="textPrimary">
                                        {message["fromUser"]}
                                    </Typography>
                                    {" — " + message["content"]}
                                </React.Fragment>
                            }
                        />
                    </ListItem>
            }
        });

        return (
            <div>

                <List className={classes.root}>
            {GroupsOfMessages}
                </List>
                <form onSubmit={this.submit}>
                    <input type="text" onChange={this.onChangeMessage}/>
                    <button type="submit">Submit</button>
                </form>
            </div>
        );
    }
}
MailListReceive.propTypes = {
    classes: PropTypes.object.isRequired,
};
export default withStyles(styles)(MailListReceive);
